    public static ArrayList<TweetModel> extractTweets() {
        ArrayList<TweetModel> tweets = new ArrayList<>();

        try {
            JSONObject jsonObject = new JSONObject(SAMPLE_JSON);
            JSONArray statusesArray = jsonObject.getJSONArray("statuses");
            JSONObject currentStatus;

            int totalStatuses = statusesArray.length();

            for (int i = 0; i < totalStatuses; i++) {
                currentStatus = statusesArray.getJSONObject(i);
                tweets.add(new TweetModel(
                        currentStatus.getJSONObject("user").getString("name"),
                        currentStatus.getJSONObject("user").getString("screen_name"),
                        currentStatus.getString("text"),
                        currentStatus.getString("created_at"),
                        currentStatus.getJSONObject("user").getString("profile_image_url_https"),
                        currentStatus.getJSONObject("user").getString("profile_background_image_url_https"),
                        String.valueOf(currentStatus.getInt("retweet_count")),
                        String.valueOf(currentStatus.getInt("favorite_count")))
                );
                Log.v(LOG_TAG, tweets.get(i).toString());
            }
        } catch (JSONException e) {
            e.printStackTrace();
        }
        return tweets;
    }
